catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }

   //Type appropriate comment here, and begin script below
	var catName = newValue+'';
	var catsys_id = g_form.getValue('cat_item');

	var ga = new GlideAjax('Nutanix_ClientServerScriptBridge');
	ga.addParam('sysparm_name', 'checkDuplicate');
	ga.addParam('sysparm_catName', catName);
	ga.addParam('sysparm_catSysId', catsys_id);
	ga.getXML(processBlueprint);

	function processBlueprint(response) {
		var answer = response.responseXML.documentElement.getAttribute("answer");

		//g_form.addErrorMessage('Catalog name is in use. Please provide some unique name or append a version number.');
		g_form.setValue('is_duplicate', answer+'');

		if(answer == 'no'){
			g_form.clearMessages();
		}else{
			g_form.showErrorBox('item_name', 'Catalog name is in use. Please provide some unique name or append a version number.');
		}

	}   


}
