function ifScript() {
	var utils = new Nutanix_Calm_Utils();
	var logger = new Nutanix_Calm_Logger();
	var cloned_app_uuid = workflow.scratchpad.cloned_app_uuid + '';
	logger.info('[wf_workflow] Getting cloned app');
	
	if(cloned_app_uuid != '') {
		
		var cloneResponse = utils.makeCalmApiCall('GET', '/apps/'+cloned_app_uuid, '');
		var app_state = JSON.parse(cloneResponse.getBody()).status.state + '';
		if(app_state == 'PENDING' || app_state == 'provisioning')
			return 'yes';
		else {
			utils.insertIntoQueue("processImportedApp", 'GET', '', cloneResponse.getBody(), 'Process applications', 'This job processes the imported applications.');
			var body = {"filter": "application_reference=="+cloned_app_uuid+";(type==action_runlog,type==audit_runlog)"};
				
			var response = utils.makeCalmApiCall('POST', '/apps/' + cloned_app_uuid + '/app_runlogs/list', JSON.stringify(body));
				
			if(response.getStatusCode() == '200' || response.getStatusCode() == '201') {
				
				var parseResp = JSON.parse(response.getBody());	
				
				for(var i = 0; i<parseResp.entities.length; i++) {
					utils.insertAppRunlogs(parseResp.entities[i]); // inserting app run logs into App Runlogs table
				}
				logger.info("[wf_workflow] App Runlogs updated for the cloned app");
			}
				return 'no';
			}
		}
		return 'no';
	}

