function ifScript() {
	var app_config = new GlideRecord('x_nuta2_nutanix_ca_calm_plugin_configuration');
	app_config.get(gs.getProperty('x_nuta2_nutanix_ca.nutanix.application.properties.sys_id'));
	if (app_config.actions_approval_workflow) {
		if(app_config.user_actions && current.actionsysid.type=='user'){
			workflow.scratchpad.user_action = app_config.user_actions;
			workflow.scratchpad.user_workflow = app_config.getValue('workflows_for_user_actions')+'';
			return 'yes';
		}
		if(app_config.system_actions && current.actionsysid.type=='system'){
			workflow.scratchpad.system_actions = app_config.system_actions;
			workflow.scratchpad.system_workflow = app_config.getValue('workflows_for_system_actions')+'';
			return 'yes';
		}
		
	}
	return 'no';
}

