var utils = new Nutanix_Calm_Utils();
var logger = new Nutanix_Calm_Logger();
var actUtils = new Nutanix_Action_Utils();

var userId = workflow.scratchpad.userID+'';
var actSysid = current.actionsysid + '';
var actionResp = '';
var runlog_uuid = '';
var app_uuid = '';
var action_uuid = '';
var action_vars = current.action_json + '';
var action_name = '';

var actionObj = new GlideRecord('x_nuta2_nutanix_ca_nutanix_calm_action');
actionObj.addQuery('sys_id', actSysid + '');
actionObj.query();
if(actionObj.next()) {
	app_uuid = actionObj.parent_uuid + '';
	action_uuid = actionObj.uuid + '';
	action_name = actionObj.name + '';
}

var appResp = utils.makeCalmApiCall('GET', '/apps/'+app_uuid, '');
var parsedAppResp = JSON.parse(appResp.getBody());

logger.info("Performing Action for "+action_name+'');

switch(action_name) {
		
	case 'action_delete':
		actionResp = utils.makeIntermediateApiCall('DELETE', '/apps/'+app_uuid, '', userId+'');
		runlog_uuid = JSON.parse(actionResp.getBody()).status.runlog_uuid;
		break;
		
	case 'action_soft_delete':
		actionResp = utils.makeIntermediateApiCall('DELETE', '/apps/'+ app_uuid + '?type=soft', '', userId+'');
		runlog_uuid = JSON.parse(actionResp.getBody()).status.runlog_uuid;
		break;
		
	case 'action_clone':
		var actBody = actUtils.app_clone_body(parsedAppResp, action_vars) + '';
		actionResp = utils.makeIntermediateApiCall('POST', '/apps/' + app_uuid + '/clone', actBody, userId+'');
		runlog_uuid = JSON.parse(actionResp.getBody()).status.runlog_uuid;
		break;
		
	case 'action_snapshot_create':
		var actBody = actUtils.snapshot_crd_body(parsedAppResp, action_vars);
		var substrat_uuid = actBody.substratUUID + '';
		actionResp = utils.makeIntermediateApiCall('POST', '/apps/' + app_uuid + '/substrates/' + substrat_uuid+'' + '/snapshot_create', JSON.stringify(actBody.actionBody), userId+'');
		runlog_uuid = JSON.parse(actionResp.getBody()).status.runlog_uuid;
		break;
	
	case 'action_snapshot_delete':
		var actBody = actUtils.snapshot_crd_body(parsedAppResp, action_vars);
		var substrat_uuid = actBody.substratUUID + '';
		actionResp = utils.makeIntermediateApiCall('POST', '/apps/' + app_uuid + '/substrates/' + substrat_uuid+'' + '/snapshot_delete', JSON.stringify(actBody.actionBody), userId+'');
		runlog_uuid = JSON.parse(actionResp.getBody()).status.runlog_uuid;
		break;
		
	case 'action_restore':
		var actBody = actUtils.snapshot_crd_body(parsedAppResp, action_vars);
		var substrat_uuid = actBody.substratUUID + '';
		actionResp = utils.makeIntermediateApiCall('POST', '/apps/' + app_uuid + '/substrates/' + substrat_uuid+'' + '/restore', JSON.stringify(actBody.actionBody), userId+'');
		runlog_uuid = JSON.parse(actionResp.getBody()).status.runlog_uuid;
		break;
		
	case 'action_update':
		var actBody = actUtils.update_actBody(parsedAppResp) + '';
		actionResp = utils.makeIntermediateApiCall('POST', '/apps/' + app_uuid + '/blueprints/new/apply',actBody , userId+'');
		runlog_uuid = JSON.parse(actionResp.getBody()).status.runlog_uuid;
		break;
		
	case 'action_image_create':
		var actBody = actUtils.image_create_body(parsedAppResp, action_vars);
		var substrat_uuid = actBody.substratUUID + '';
		actionResp = utils.makeIntermediateApiCall('POST', '/apps/' + app_uuid + '/substrates/' + substrat_uuid+'' + '/image_create', JSON.stringify(actBody.actionBody), userId+'');
		runlog_uuid = JSON.parse(actionResp.getBody()).status.runlog_uuid;
		break;
		
	default:
		var actBody = actUtils.default_actbody(parsedAppResp, action_vars) + '';
		actionResp = utils.makeIntermediateApiCall('POST', '/apps/' + app_uuid + '/actions/' + action_uuid+'' + '/run', actBody, userId+'');
		runlog_uuid = JSON.parse(actionResp.getBody()).status.runlog_uuid;
		break;
}

if(actionResp.getStatusCode() == '200' || actionResp.getStatusCode() == '201') {
	
	var gr = new GlideRecord('x_nuta2_nutanix_ca_nutanix_calm_application');
	gr.initialize();
	gr.addQuery('uuid', app_uuid);
	gr.query();
	if(gr.next()) {
		gr.action_flag = '1';
		gr.update();
	}
	
	var runlogResp = utils.makeCalmApiCall('GET', '/apps/' + app_uuid + '/app_runlogs/' + runlog_uuid, '');
	var actionState = JSON.parse(runlogResp.getBody()).status.state + '';
	
	workflow.scratchpad.actionState = actionState + '';
	workflow.scratchpad.app_uuid = app_uuid + '';
	workflow.scratchpad.runlog_uuid = runlog_uuid + '';
	workflow.scratchpad.action_name = action_name + '';
	
} else {
	
	logger.error("Application action failed: "+JSON.stringify(runlogResp));
	
}

