var utils = new Nutanix_Calm_Utils();
var logger = new Nutanix_Calm_Logger();
var reqUtils = new  Nutanix_RequestUtil();
var bp_uuid = workflow.scratchpad.bp_uuid;

//gs.error("before launch :: "+bp_uuid);
logger.info("[wf_workflow] Marketplace_launch has been completed by '"+workflow.scratchpad.user+"', Blueprint uuid: "+bp_uuid+", Name: '"+workflow.scratchpad.bp_name+"'");

var response = utils.makeCalmApiCall('GET', '/blueprints/'+bp_uuid, '');
if(response.getStatusCode() == '200' || response.getStatusCode() == '201') {
	var parseResp = JSON.parse(response.getBody());
	//gs.error("BP UUID body recieved:: "+response.getBody());
	
	var app_profile_uuid = ' ';
	var app_profile_name = (current.variables.default_profile.getDisplayValue()).trim();
	
	for(var i = 0; i<parseResp.spec.resources.app_profile_list.length; i++){
		var res_app_profile = (parseResp.spec.resources.app_profile_list[i].name).toString();
				
		if(app_profile_name == res_app_profile){
			app_profile_uuid = parseResp.spec.resources.app_profile_list[i].uuid;
			//gs.error("app_profile_uuid and name MPI::"+app_profile_name+'::'+app_profile_uuid);
		}
		
	}
	
	var project_name = ' ';
	var project_uuid = current.variables.default_project+'';
	var grProj = new GlideRecord('x_nuta2_nutanix_ca_nutanix_project');
	grProj.addQuery('uuid', project_uuid);
	grProj.query();
	if(grProj.next()){
		project_name = grProj.name;
	}
	
	parseResp.metadata.project_reference = {
		"name":project_name+'',
		"kind":"project" ,
		"uuid":project_uuid+''
	};
	
	
	/*
	var userSysID = gs.getUserID();
	var grUser = new GlideRecord('sys_user');
	grUser.addQuery('sys_id', userSysID);
	grUser.query();
	if(grUser.next()){
		
		var name = grUser.user_name;
		var uuid = grUser.uuid;
		
	}
	
	
	parseResp.metadata.owner_reference = {
		"name":name+'',
		"kind":"user",
		"uuid":uuid+''
	};
	
	*/
	
	
	
	var tmpbody = {
		"status": parseResp.status,
		"spec": parseResp.spec,
		"metadata" : parseResp.metadata
	};
		
//	tmpbody = reqUtils.transformTempBody(tmpbody, JSON.parse(current.cat_item.getRefRecord().cloned_bp_json));
	
	var body =
	{
		"api_version":parseResp.api_version,
		"metadata":parseResp.metadata,
		"spec" :{
			"app_profile_reference": {
				"kind": "app_profile",
				"uuid": app_profile_uuid+''                       //current.variables.default_profile+''
			},
			
			"application_name": current.variables.default_app_name+'',
			"description":parseResp.status.description,
			"resources": reqUtils.makeResourceBody(tmpbody, tmpbody.status.resources, current)
			//reqUtils.makeResourceBody(tmpbody, tmpbody.status.resources, current)
			//parseResp.spec.resources
		}
	};
	
// 	gs.error(" ::: " + JSON.stringify(parseResp));
 	gs.error("BP payload send::"+JSON.stringify(body));
	
	var bpLaunchedResp = utils.makeIntermediateApiCall('POST', '/blueprints/'+bp_uuid+'/launch', JSON.stringify(body), workflow.scratchpad.userID+'');
	
	workflow.scratchpad.bpLaunchedResp = bpLaunchedResp;
	
} else {
	workflow.scratchpad.blueprint_launch_failed = response.getBody();	
	logger.error("[wf_workflow] Blueprint '"+workflow.scratchpad.bp_name+"' Launch failed by '"+gs.getUserName()+"', Reason: "+response.getBody());
	
}

