  answer = ifScript();

  function ifScript() {
	  var logger = new Nutanix_Calm_Logger();	
	  
	  var appState = workflow.scratchpad.appState;
	  
     if (appState == 'PENDING' || appState == 'provisioning') {
		 logger.info("[wf_workflow]  Application '"+workflow.scratchpad.app_name+"' Provisioning is in progress state: "+appState);
		 
		 current.stage = 'provisioning';
		 
        return 'yes';
     }
     return 'no';
  }

