function ifScript() {
	var logger = new Nutanix_Calm_Logger();
	
	var appState = workflow.scratchpad.appState;
	
	if (appState == 'error') {
		logger.info("[wf_workflow]  Application '"+workflow.scratchpad.app_name+"' is into '"+appState+"' state");
		
		current.state = 4; //make Requested Item's state as 'Closed Incomplete'
		current.stage = 'error';
		
		return 'yes';
	} else {
		logger.info("[wf_workflow]  Application '"+workflow.scratchpad.app_name+"' is into '"+appState+"' state");
		current.state = 3; //make Requested Item's state as 'Closed Complete'
		current.stage = 'complete';
		
		return 'no';
	}
}

