function ifScript() {
	
	current.state = 2; // to make the RITM's sate as 'Work In Progress'
	current.stage = 'provisioning';
	
	var logger = new Nutanix_Calm_Logger();
	workflow.scratchpad.cat_item = current.cat_item;
	workflow.scratchpad.user = gs.getUserName();
// 	workflow.scratchpad.userID = gs.getUserID();
// 	gs.info("User ID: "+gs.getUserID());
	
	if (current.cat_item.blueprint_name.sys_class_name == 'x_nuta2_nutanix_ca_nutanix_blueprint') {
		return 'yes';
	}
	return 'no';
	
	
}

// This script needs to set answer to 'yes' or 'no' to indicate the state of the activity.
//
// For example,
//
answer = ifScript();

function ifScript() {
	var logger = new Nutanix_Calm_Logger();	
	
	var bpStatusResp = workflow.scratchpad.bpStatusResp; 
	
	var parsedJson =  JSON.parse(bpStatusResp.getBody());	
	
	
	if((parsedJson.status.state == 'success' || parsedJson.status.state == 'Success') && (parsedJson.status.milestone == 'Succeeded' || parsedJson.status.milestone == 'succeeded')){
		
		workflow.scratchpad.app_uuid = parsedJson.status.application_uuid;		
		logger.info("[wf_workflow] Application uuid: '"+workflow.scratchpad.app_uuid+"' has been created for Blueprint '"+workflow.scratchpad.bp_name+"'");
		workflow.scratchpad.app_name = parsedJson.status.app_name;
		
		return 'yes';
		
	} else if((parsedJson.status.state == 'failure' || parsedJson.status.state == 'Failure') && (parsedJson.status.milestone == 'failed' || parsedJson.status.milestone == 'Failed')){
		
		workflow.scratchpad.blueprint_launch_failed = bpStatusResp.getBody();
		
		logger.error("[wf_workflow] Blueprint '"+workflow.scratchpad.bp_name+"' Launch failed by '"+workflow.scratchpad.user+"', Reason: "+workflow.scratchpad.blueprint_launch_failed);
		
		return 'no';
	} 
	
	
}

