function ifScript() {
	var logger = new Nutanix_Calm_Logger();
	var requestState = workflow.scratchpad.requestState;
	if (requestState == 'PENDING' || requestState == 'pending' || requestState == 'running') {			
		logger.info("[wf_workflow] Blueprint '"+workflow.scratchpad.bp_name+"' Launch is in progress state: "+requestState);
		
		return 'yes';
	}
	return 'no';
}

