var logger = new Nutanix_Calm_Logger();
var utils = new Nutanix_Calm_Utils();

current.state = 4; //make Requested Item's state as 'Closed Incomplete'
current.stage = 'error';

var catItemName = ' ';
var gr = new GlideRecord('sc_cat_item');
gr.addQuery('sys_id',workflow.scratchpad.cat_item);
gr.query();
if(gr.next())
{
	catItemName = gr.name;
}
if(gs.getProperty('x_nuta2_nutanix_ca.incident.blueprint.failed') == 'true')
{
	
	var grInc = new GlideRecord('incident');
	grInc.initialize();
	grInc.caller_id = workflow.scratchpad.userID + '';
	grInc.x_nuta2_nutanix_ca_catalog_item = workflow.scratchpad.cat_item;
	
	
	var message = workflow.scratchpad.app_name+' has provisioned to an error state';
	var desc = '';
	var reason = '';
	var output_JSON = 
	{
		"task_name" : [],
		"detailed_output" : []
	};
	
	var app_uuid = workflow.scratchpad.app_uuid;
	var body = 
	{
		"filter": "application_reference=="+app_uuid+";type==action_runlog"
	};
	
	var response = utils.makeCalmApiCall('POST', '/apps/' + app_uuid + '/app_runlogs/list', JSON.stringify(body));
	
	if(response.getStatusCode() == '200' || response.getStatusCode() == '201') 
	{
		
		var parseResp = JSON.parse(response.getBody());
		gs.info("@@@ 1: " + JSON.stringify(parseResp));
		for(var i in parseResp.entities)
			var rootRefUuuid =  parseResp.entities[i].metadata.uuid;
		
		var body2 = 
		{
			"filter": "root_reference=="+rootRefUuuid
		};
		
		var response2 = utils.makeCalmApiCall('POST', '/apps/' + app_uuid + '/app_runlogs/list', JSON.stringify(body2));
		
		var parseResp2 = JSON.parse(response2.getBody());
		gs.info("@@@ 2: " + JSON.stringify(parseResp2));
		for(var j in parseResp2.entities) 
		{
			
			var entitiesStatus = parseResp2.entities[j].status;
			if(parseResp2.entities[j].status.task_reference && parseResp2.entities[j].status.task_reference.name!='') 
			{
				
				var response3 = utils.makeCalmApiCall('GET', '/apps/' + app_uuid + '/app_runlogs/'+parseResp2.entities[j].metadata.uuid+'/output');
				var parseResp3 = JSON.parse(response3.getBody());
				gs.info("@@@ 3: " + JSON.stringify(parseResp3));
				desc = parseResp2.entities[j].status.task_reference.name;
				gs.info("@@@ Desc 1: " + desc);
				output_JSON.task_name.push(parseResp2.entities[j].status.task_reference.name);
				output_JSON.detailed_output.push(JSON.parse(response3.getBody()).status.output_list[0].output);
				
			}
			if(parseResp2.entities[j].status.reason_list!='')
			{
				reason = (parseResp2.entities[j].status.reason_list).toString();
				gs.info("@@@ reason 1: " + reason);
				
			}
			
		}
	}
	
	grInc.short_description = 'Runlogs for application '+workflow.scratchpad.app_name;
	gs.info("@@@ SD: " + grInc.short_description);
	//gs.info("@@@ Desc: " + desc);
	grInc.description = 'Task Name: ' + desc + '\n\n Reason' + reason; //updating description.
	gs.info("@@@ Description: " + grInc.description);
	grInc.assignment_group =  gs.getProperty('x_nuta2_nutanix_ca.assignment.group');
	var incidentSysID = grInc.insert();
	for(i in output_JSON.task_name) 
	{
		var gsa = new GlideSysAttachment();
		var attachmentId = gsa.write(grInc, output_JSON.task_name[i]+".txt", 'text/plain', output_JSON.detailed_output[i]);
	}
	
	var grItem = new GlideRecord('sc_req_item');
	grItem.addQuery('request.number', current.request.getDisplayValue());
	grItem.query();
	if(grItem.next())
	{		
		grItem.x_nuta2_nutanix_ca_incident = incidentSysID;
		grItem.update();				
	}
	
	
} 
else 
{
	logger.info("[wf_workflow] Blueprint '"+catItemName+"' launch failed. No incident will be created because Create Incidents property is unchecked by Calm Admin");
}

