var logger = new Nutanix_Calm_Logger();
var utils = new Nutanix_Calm_Utils();

current.state = 4; //make Requested Item's state as 'Closed Incomplete'
 current.stage = 'error';

var catItemName = ' ';
var gr = new GlideRecord('sc_cat_item');
gr.addQuery('sys_id',workflow.scratchpad.cat_item);
gr.query();
if(gr.next()){
	catItemName = gr.name;
}
if(gs.getProperty('x_nuta2_nutanix_ca.incident.blueprint.failed') == 'true'){
	
	var grInc = new GlideRecord('incident');
	grInc.initialize();
	grInc.caller_id = workflow.scratchpad.userID + '';
	grInc.x_nuta2_nutanix_ca_catalog_item = workflow.scratchpad.cat_item;
	
	
	var failedResp = workflow.scratchpad.blueprint_launch_failed;
	var parseJson = JSON.parse(failedResp);
	
	var message = '';
	var desc = '';
	var reason = '';
	
	if(parseJson.code && parseJson.state){
		message = "Blueprint Catalog Name: "+catItemName+"\nCode: "+parseJson.code+"\n"+"State: "+parseJson.state;
		for(var i = 0; i<parseJson.message_list.length; i++){
			desc = (parseJson.message_list[i].message)?parseJson.message_list[i].message:'';
			reason = (parseJson.message_list[i].reason)?parseJson.message_list[i].reason:'';
			message = message+"\nError Message: "+desc+" \nReason: "+reason;
		}
		
		grInc.description = message;
		
	}else if(parseJson.status.state && parseJson.status.milestone){
		message = "Blueprint Catalog Name: "+catItemName+"\n"+"State: "+parseJson.status.state;
		
		for(var i = 0; i<parseJson.status.message_list.length; i++){
			
			desc = (parseJson.status.message_list[i].message)?parseJson.status.message_list[i].message:'';
			reason = (parseJson.status.message_list[i].reason)?parseJson.status.message_list[i].reason:'';			
			var details = (parseJson.status.message_list[i].details)?parseJson.status.message_list[i].details:'';
			
			message = message+"\nError Message: "+desc+" \nReason: "+reason+" \nDetails: "+JSON.stringify(details);
		}
		grInc.description = message;		
	} else {
		grInc.description = workflow.scratchpad.blueprint_launch_failed;
	}
	
	grInc.assignment_group =  gs.getProperty('x_nuta2_nutanix_ca.assignment.group');
	grInc.short_description = catItemName;
	var incidentSysID = grInc.insert();
	var gsa = new GlideSysAttachment();
	var attachmentId = gsa.write(grInc, "Incident_attachment.txt", 'text/plain', message+'');
	
	var grItem = new GlideRecord('sc_req_item');
	grItem.addQuery('request.number', current.request.getDisplayValue());
	grItem.query();
	if(grItem.next()){		
		grItem.x_nuta2_nutanix_ca_incident = incidentSysID;
		grItem.update();				
	}
	
	logger.error("[wf_workflow] Incident created on Blueprint '"+catItemName+"' launched failure");
	
} else {	
	logger.info("[wf_workflow] Blueprint '"+catItemName+"' launched failed. No incident will be created because Create Incidents property is unchecked by Calm Admin");	
}

