var utils = new Nutanix_Calm_Utils();
var logger = new Nutanix_Calm_Logger();
var request_id = workflow.scratchpad.request_id;
var bp_uuid = workflow.scratchpad.bp_uuid;
var bpStatusResp = utils.makeCalmApiCall('GET', '/blueprints/'+bp_uuid+'/pending_launches/'+request_id);

if(bpStatusResp.getStatusCode() == '200' || bpStatusResp.getStatusCode() == '201') {	
	workflow.scratchpad.requestState = JSON.parse(bpStatusResp.getBody()).status.state + '';
	workflow.scratchpad.bpStatusResp = bpStatusResp;
	
} else {	
	logger.error("[wf_workflow] Blueprint '"+workflow.scratchpad.bp_name+"' Pending Launches failed by '"+workflow.scratchpad.user+"', Reason: "+bpStatusResp.getBody());
}

