//
// For example,

answer = ifScript();

function ifScript() {
	var utils = new Nutanix_Calm_Utils();
	var logger = new Nutanix_Calm_Logger();
	
	var bpLaunchedResp  = workflow.scratchpad.bpLaunchedResp;
	
	if(bpLaunchedResp.getStatusCode() == '200' || bpLaunchedResp.getStatusCode() == '201'){
	
		bpLaunchedResp = JSON.parse(bpLaunchedResp.getBody());
		workflow.scratchpad.request_id = bpLaunchedResp.status.request_id;
		
		logger.info("[wf_workflow] Blueprint '"+workflow.scratchpad.bp_name+"' Launched Success by '"+workflow.scratchpad.user+"'");
		logger.info("[wf_workflow] Request ID: "+workflow.scratchpad.request_id+" for Blueprint: "+ bpLaunchedResp.metadata.name+''+" has been created");
		
		
		return 'yes';
	} else{		
		workflow.scratchpad.blueprint_launch_failed = bpLaunchedResp.getBody();
		logger.error("[wf_workflow] Blueprint '"+workflow.scratchpad.bp_name+"' Launch failed by '"+workflow.scratchpad.user+"', Reason: "+bpLaunchedResp.getBody());
		return 'no';
	}
	
}

