(function() {
	// return the value to use for Parameter 1
	var utils = new Nutanix_Calm_Utils();
	var app_uuid = workflow.scratchpad.app_uuid;
	var appUuidResponse = utils.makeCalmApiCall('GET', '/apps/'+app_uuid, '');
	var parsedUuidResponse = JSON.parse(appUuidResponse.getBody());
	
	var mailJSON = {"cloned_from":"","services":[]};
	
	if(parsedUuidResponse.status.resources.cloned_from_reference)
		mailJSON.cloned_from = parsedUuidResponse.status.resources.cloned_from_reference.name + '';
	
	var dep_list = parsedUuidResponse.status.resources.deployment_list;
	
	for(var j in dep_list) {
		var elements = (dep_list[j].substrate_configuration && dep_list[j].substrate_configuration.element_list) ? dep_list[j].substrate_configuration.element_list : {} ;
		
		for(var p in elements)
			mailJSON.services.push({"name":elements[p].name+'',"ip":elements[p].address+''});
	}
	
	return JSON.stringify(mailJSON);
}());

