	utils.insertIntoQueue("processImportedApp", 'GET', current.request.requested_for, appUuidResponse.getBody(), 'Process applications', 'This job processes the imported applications.');
	
} else {
	logger.error("[wf_workflow] Error during retrieval of application '"+workflow.scratchpad.app_name+"' actions to API /apps/<uuid> Error : "+appUuidResponse.getBody());
}

